import React, { useState, useEffect } from 'react';
import { ProjectMembershipForm } from './ProjectMembershipForm';

function ProjectMembershipList() {
  const [memberships, setMemberships] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('/projects/')
      .then(response => response.json())
      .then(data => setMemberships(data))
      .catch(error => console.error('Error fetching memberships:', error));

    fetch('/projects')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.error('Error fetching projects:', error));

    fetch('/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const refreshMemberships = () => {
    fetch('/projects/')
      .then(response => response.json())
      .then(data => setMemberships(data))
      .catch(error => console.error('Error fetching memberships:', error));
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Project Memberships</h2>
      <div className="row row-cols-1 row-cols-md-2 g-4">
        <div className="col">
          <ul className="list-group">
            {memberships.map(membership => (
              <li key={membership.id} className="list-group-item d-flex justify-content-between align-items-center">
                {membership.user} - {membership.project}
                <span className="badge bg-success">{membership.role}</span>
              </li>
            ))}
          </ul>
        </div>
        <div className="col">
          <h3 className="text-center mb-4">Add New Membership</h3>
          <ProjectMembershipForm
            projects={projects}
            users={users}
            onMembershipAdded={refreshMemberships}
          />
        </div>
      </div>
    </div>
  );
}

export default ProjectMembershipList;
