import React, { useState, useEffect } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

function ProjectMembershipList() {
  const [memberships, setMemberships] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchMemberships();
    fetchProjects();
    fetchUsers();
  }, []);

  const fetchMemberships = () => {
    fetch('/projects/')
      .then(response => response.json())
      .then(data => setMemberships(data))
      .catch(error => console.error('Error fetching memberships:', error));
  };

  const fetchProjects = () => {
    fetch('/projects')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.error('Error fetching projects:', error));
  };

  const fetchUsers = () => {
    fetch('/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  };

  const refreshMemberships = () => {
    fetchMemberships();
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Project Memberships</h2>
      <div className="grid md:grid-cols-2 gap-8">
        <div>
          <ul className="bg-white shadow overflow-hidden sm:rounded-md">
            {memberships.map(membership => (
              <li key={membership.id} className="border-b border-gray-200 last:border-b-0">
                <div className="px-4 py-4 sm:px-6">
                  <div className="flex items-center justify-between">
                    <p className="text-sm font-medium text-indigo-600 truncate">
                      {membership.user} - {membership.project}
                    </p>
                    <div className="ml-2 flex-shrink-0 flex">
                      <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                        {membership.role}
                      </p>
                    </div>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h3 className="text-xl font-semibold mb-4">Add New Membership</h3>
          <ProjectMembershipForm
            projects={projects}
            users={users}
            onMembershipAdded={refreshMemberships}
          />
        </div>
      </div>
    </div>
  );
}

function ProjectMembershipForm({ projects, users, onMembershipAdded }) {
  const initialValues = {
    projectId: '',
    userId: '',
    role: ''
  };

  const validationSchema = Yup.object({
    projectId: Yup.string().required('Project is required'),
    userId: Yup.string().required('User is required'),
    role: Yup.string().required('Role is required')
  });

  const onSubmit = (values, { resetForm }) => {
    const membership = {
      project: projects.find(project => project.id === values.projectId).name,
      user: users.find(user => user.id === values.userId).name,
      role: values.role
    };

    fetch('/projects/memberships', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(membership)
    })
      .then(response => response.json())
      .then(data => {
        resetForm();
        onMembershipAdded();
      })
      .catch(error => console.error('Error adding membership:', error));
  };

  return (
    <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
      <Form className="bg-white shadow overflow-hidden sm:rounded-md p-4">
        <div className="grid grid-cols-1 gap-y-6">
          <div>
            <label htmlFor="projectId" className="block text-sm font-medium text-gray-700">Project</label>
            <Field
              as="select"
              id="projectId"
              name="projectId"
              className="mt-1 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
            >
              <option value="">Select Project</option>
              {projects.map(project => (
                <option key={project.id} value={project.id}>{project.name}</option>
              ))}
            </Field>
            <ErrorMessage name="projectId" component="p" className="mt-2 text-sm text-red-600" />
          </div>

          <div>
            <label htmlFor="userId" className="block text-sm font-medium text-gray-700">User</label>
            <Field
              as="select"
              id="userId"
              name="userId"
              className="mt-1 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
            >
              <option value="">Select User</option>
              {users.map(user => (
                <option key={user.id} value={user.id}>{user.name}</option>
              ))}
            </Field>
            <ErrorMessage name="userId" component="p" className="mt-2 text-sm text-red-600" />
          </div>

          <div>
            <label htmlFor="role" className="block text-sm font-medium text-gray-700">Role</label>
            <Field
              type="text"
              id="role"
              name="role"
              className="mt-1 block w-full shadow-sm sm:text-sm border-gray-300 rounded-md"
            />
            <ErrorMessage name="role" component="p" className="mt-2 text-sm text-red-600" />
          </div>

          <button
            type="submit"
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Add Membership
          </button>
        </div>
      </Form>
    </Formik>
  );
}

export default ProjectMembershipList;
